<?phpnamespace GrandConferenceElementor\Widgets;use Elementor\Widget_Base;use Elementor\Controls_Manager;if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly/** * Elementor Portfolio Classic * * Elementor widget for portfolio posts * * @since 1.0.0 */class GrandConference_Classic_Testimonials_Column extends Widget_Base {	/**	 * Retrieve the widget name.	 *	 * @since 1.0.0	 *	 * @access public	 *	 * @return string Widget name.	 */	public function get_name() {		return 'grandconference-classic-testimonials-column';	}	/**	 * Retrieve the widget title.	 *	 * @since 1.0.0	 *	 * @access public	 *	 * @return string Widget title.	 */	public function get_title() {		return __( 'Classic Testimonials Columns', 'grandconference-elementor' );	}	/**	 * Retrieve the widget icon.	 *	 * @since 1.0.0	 *	 * @access public	 *	 * @return string Widget icon.	 */	public function get_icon() {		return 'eicon-post-list';	}	/**	 * Retrieve the list of categories the widget belongs to.	 *	 * Used to determine where to display the widget in the editor.	 *	 * Note that currently Elementor supports only one category.	 * When multiple categories passed, Elementor uses the first one.	 *	 * @since 1.0.0	 *	 * @access public	 *	 * @return array Widget categories.	 */	public function get_categories() {		return [ 'grandconference-theme-classic-widgets-category' ];	}	/**	 * Retrieve the list of scripts the widget depended on.	 *	 * Used to set scripts dependencies required to run the widget.	 *	 * @since 1.0.0	 *	 * @access public	 *	 * @return array Widget scripts dependencies.	 */	public function get_script_depends() {		return array();	}	/**	 * Register the widget controls.	 *	 * Adds different input fields to allow the user to change and customize the widget settings.	 *	 * @since 1.0.0	 *	 * @access protected	 */	protected function register_controls() {		$this->start_controls_section(			'section_content',			[				'label' => __( 'Content', 'grandconference-elementor' ),			]		);				$this->add_control(			'cat',			[				'label' => __( 'Filter by category', 'grandconference-elementor' ),				'type' => Controls_Manager::SELECT2,				'options' => grandconference_get_testimonial_cat(),				'multiple' => false,			]		);				$this->add_control(			'columns',			[				'label' => __( 'Columns', 'grandconference-elementor' ),				'type' => Controls_Manager::SLIDER,				'default' => [					'size' => 2,				],				'range' => [					'px' => [						'min' => 2,						'max' => 4,						'step' => 1,					]				],			]		);				$this->add_control(			'items',			[				'label' => __( 'Items', 'grandconference-elementor' ),				'type' => Controls_Manager::SLIDER,				'default' => [					'size' => 10,				],				'range' => [					'px' => [						'min' => 1,						'max' => 500,						'step' => 1,					]				],			]		);				$this->add_responsive_control(			'thumbnail_border_radius',			[				'label' => __( 'Thumbnail Border Radius', 'grandconference-elementor' ),				'type' => Controls_Manager::DIMENSIONS,				'size_units' => [ 'px' ],				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_wrapper .testimonial_image' => 'border-radius: {{TOP}}{{UNIT}} {{RIGHT}}{{UNIT}} {{BOTTOM}}{{UNIT}} {{LEFT}}{{UNIT}};',				],			]		);				$this->end_controls_section();				$this->start_controls_section(			'section_content_style',			array(				'label'      => esc_html__( 'Content', 'grandconference-elementor' ),				'tab'        => Controls_Manager::TAB_STYLE,				'show_label' => false,			)		);				$this->add_group_control(				\Elementor\Group_Control_Typography::get_type(),				[					'name' => 'content_typography',					'label' => __( 'Content Typography', 'grandconference-elementor' ),					'selector' => '{{WRAPPER}} .testimonials-column-wrapper .testimonial_wrapper .testimonial_content',				]			);				$this->add_control(			'content_color',			[				'label' => __( 'Content Color', 'grandconference-elementor' ),				'type' => Controls_Manager::COLOR,				'default' => '#000000',				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_wrapper .testimonial_content' => 'color: {{VALUE}}',				],			]		);				$this->end_controls_section();				$this->start_controls_section(			'section_name_style',			array(				'label'      => esc_html__( 'Name', 'grandconference-elementor' ),				'tab'        => Controls_Manager::TAB_STYLE,				'show_label' => false,			)		);				$this->add_group_control(				\Elementor\Group_Control_Typography::get_type(),				[					'name' => 'name_typography',					'label' => __( 'Name Typography', 'grandconference-elementor' ),					'selector' => '{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer h6',				]			);				$this->add_control(			'name_color',			[				'label' => __( 'Name Color', 'grandconference-elementor' ),				'type' => Controls_Manager::COLOR,				'default' => '#000000',				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer h6' => 'color: {{VALUE}}',				],			]		);				$this->end_controls_section();				$this->start_controls_section(			'section_position_style',			array(				'label'      => esc_html__( 'Position', 'grandconference-elementor' ),				'tab'        => Controls_Manager::TAB_STYLE,				'show_label' => false,			)		);				$this->add_group_control(			\Elementor\Group_Control_Typography::get_type(),			[				'name' => 'position_typography',				'label' => __( 'Position Typography', 'grandconference-elementor' ),				'selector' => '{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_position',			]		);				$this->add_control(			'position_color',			[				'label' => __( 'Position Color', 'grandconference-elementor' ),				'type' => Controls_Manager::COLOR,				'default' => '#000000',				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_position' => 'color: {{VALUE}}',				],			]		);				$this->end_controls_section();				$this->start_controls_section(			'section_company_style',			array(				'label'      => esc_html__( 'Company', 'grandconference-elementor' ),				'tab'        => Controls_Manager::TAB_STYLE,				'show_label' => false,			)		);				$this->add_group_control(			\Elementor\Group_Control_Typography::get_type(),			[				'name' => 'company_typography',				'label' => __( 'Company Typography', 'grandconference-elementor' ),				'selector' => '{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_company',			]		);				$this->add_control(			'company_color',			[				'label' => __( 'Company Color', 'grandconference-elementor' ),				'type' => Controls_Manager::COLOR,				'default' => '#FF2D55',				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_company, {{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_company a' => 'color: {{VALUE}}',				],			]		);				$this->add_control(			'company_hover_color',			[				'label' => __( 'Company Hover Color', 'grandconference-elementor' ),				'type' => Controls_Manager::COLOR,				'default' => '#000000',				'selectors' => [					'{{WRAPPER}} .testimonials-column-wrapper .testimonial_content .testimonial_customer_company a:hover' => 'color: {{VALUE}}',				],			]		);				$this->end_controls_section();	}	/**	 * Render the widget output on the frontend.	 *	 * Written in PHP and used to generate the final HTML.	 *	 * @since 1.0.0	 *	 * @access protected	 */	protected function render() {		include(GRANDCONFERENCE_ELEMENTOR_PATH.'templates/classic-testimonials-column/index.php');	}	/**	 * Render the widget output in the editor.	 *	 * Written as a Backbone JavaScript template and used to generate the live preview.	 *	 * @since 1.0.0	 *	 * @access protected	 */	protected function content_template() {		return '';	}}